ndex.php?id=1821";}i:1820;a:29:{s:2:"id";s:4:"1820";s:9:"parent_id";s:1:"0";s:7:"cate_id";s:3:"589";s:9:"module_id";s:2:"83";s:10:"project_id";s:3:"390";s:7:"site_id";s:1:"1";s:5:"title";s:9:"一个人";s:8:"dateline";s:10:"1483415041";s:4:"sort";s:1:"0";s:6:"status";s:1:"1";s:6:"hidden";s:1:"0";s:4:"hits";s:1:"0";s:3:"tpl";s:0:"";s:9:"seo_title";s:0:"";s:12:"seo_keywords";s:0:"";s:8:"seo_desc";s:0:"";s:3:"tag";s:0:"";s:4:"attr";s:0:"";s:9:"replydate";s:1:"0";s:7:"user_id";s:1:"0";s:10:"identifier";s:0:"";s:8:"integral";s:1:"0";s:3:"pic";a:16:{s:2:"id";s:4:"1090";s:7:"cate_id";s:1:"1";s:6:"folder";s:14:"res/201701/03/";s:4:"name";s:20:"024c558f707d5f5c.jpg";s:3:"ext";s:3:"jpg";s:8:"filename";s:34:"res/201701/03/024c558f707d5f5c.jpg";s:3:"ico";s:23:"res/201701/03/_1090.jpg";s:7:"addtime";s:10:"1483415077";s:5:"title";s:21:"夏婉安－一个人";s:4:"attr";a:2:{s:5:"width";i:345;s:6:"height";i:240;}s:4:"note";s:0:"";s:10:"session_id";s:0:"";s:7:"user_id";s:1:"0";s:8:"download";s:1:"0";s:8:"admin_id";s:1:"1";s:2:"gd";a:2:{s:5:"thumb";s:28:"res/201701/03/thumb_1090.jpg";s:4:"auto";s:27:"res/201701/03/auto_1090.jpg";}}s:8:"fullname";s:9:"夏婉安";s:10:"music_addr";a:16:{s:2:"id";s:4:"1089";s:7:"cate_id";s:2:"16";s:6:"folder";s:20:"res/video/201701/03/";s:4:"name";s:20:"46482c45a684102b.mp3";s:3:"ext";s:3:"mp3";s:8:"filename";s:40:"res/video/201701/03/46482c45a684102b.mp3";s:3:"ico";s:29:"images/filetype-large/mp3.jpg";s:7:"addtime";s:10:"1483415057";s:5:"title";s:21:"夏婉安 - 一个人";s:4:"attr";s:0:"";s:4:"note";s:0:"";s:10:"session_id";s:0:"";s:7:"user_id";s:1:"0";s:8:"download";s:1:"0";s:8:"admin_id";s:1:"1";s:2:"gd";N;}s:7:"content";s:1432:"<p>《一个人》歌词：</p><p>乌云一层一层的遮住了光</p><p>坚强一层一层卸掉了伪妆</p><p>过往的车辆遮掩了眼神里面的慌张</p><p>脆弱被嘴角的笑无情打伤</p><p><br/></p><p>一颗心里面住着一个人</p><p>一个人却只有着一颗心</p><p>当你来来回回践踏着我心底</p><p>我不怕痛只怕你爱的不坚定</p><p><br/></p><p>一个人吃饭一个人睡</p><p>一个人想念一个人醉</p><p>一个人爱着另一个人</p><p>一个人只流泪好 好伤悲</p><p><br/></p><p>我是一条小鱼儿</p><p>不曾想过要逃离</p><p>我也爱大海</p><p>我也想呼吸</p><p>可是想到舍弃你</p><p>我宁愿干涸在你宽大的掌心里</p><p><br/></p><p>我一个人痛，我一个人走</p><p>我一个人守候到天明</p><p>我一个人梦</p><p>我一个人错</p><p>我一个人或喜或悲不要谁关心我</p><p><br/></p><p>我一个人疯，我一个人懂</p><p>我一个人多想回过头</p><p>可明知道</p><p>你不在身后</p><p>反反复复爱到最后只剩下我</p><p><br/></p><p>我一个人走（让我一个人走）</p><p>别再说爱我（我不想你爱我）</p><p>放手了是吗（真的放你走了）</p><p>从此一个人</p><p><br/></p><p>让我一个人走（我一个人走）</p><p>我不想你爱我（别再说爱我）</p><p>真的放你走了（放手了是吗）</p><p>一个人好好的（Ha.A...)</p><p><br/></p>";s:8:"catelist";a:1:{i:589;a:4:{s:7:"cate_id";s:3:"589";s:5:"title";s:6:"流行";s:10:"identifier";s:7:"liuxing";s:3:"url";s:53:"http://localhost:8080/index.php?id=music&cate=liuxing";}}s:4:"cate";a:4:{s:7:"cate_id";s:3:"589";s:5:"title";s:6:"流行";s:10:"identifier";s:7:"liuxing";s:3:"url";s:53:"http://localhost:8080/index.php?id=music&cate=liuxing";}s:3:"url";s:39:"http://localhost:8080/index.php?id=1820";}}}